mapper.select:
select count(*)
  from sch.sch_usr
 where usr_id = #{usrId}

mapper.insert:
insert into sch.sch_usr (
                          cpn_cd
                         ,usr_id
                         ,pwd_no
                         ,usr_nm
                         ,pho_no
                         ,roles
                         ,use_yn
                         ,ins_id
                         ,ins_dttm
) values (
           lpad(nextval('cpn_seq')::varchar, 3, '0')
          ,#{usrId}
          ,#{pwdNo}
          ,#{usrNm}
          ,#{phoNo}
          ,#{roles}
          ,'Y'
          ,'YJH'
          ,now()
)
